var client *model.Client4
var user *model.User

func main() {

	client = model.NewAPIv4Client(MATTERMOST_SERVER)
	pingServer()
	login()

	getAllEmoji()
	reactAll(POST_ID)

	log.Info("Done")
}

func pingServer() {
	if props, resp := client.GetOldClientConfig(""); resp.Error != nil {
		log.Fatal("There was a problem pinging the Mattermost server.")
	} else {
		log.Info("Server detected and is running version " + props["Version"])
	}
}

func login() {
	if u, resp := client.Login(USER_EMAIL, USER_PASSWORD); resp.Error != nil {
		log.Fatal("There was a problem logging into the Mattermost server.")
	} else {
		user = u
	}
}

func getAllEmoji() {
	support_emojis = append(support_emojis, getCustomEmoji()...)
}

func getCustomEmoji() (emojis []string) {
	emoji, resp := client.GetEmojiList(0, 60)
	if resp.Error != nil {
		return
	}
	for _, e := range emoji {
		emojis = append(emojis, e.Name)
	}

	return
}

func reactAll(postID string) {
	for _, emoji := range support_emojis {
		react(postID, emoji)
	}
}

func react(postID string, emoji string) error {
	react := &model.Reaction{
		UserId:    user.Id,
		PostId:    postID,
		EmojiName: emoji,
	}
	react.PreSave()
	_, resp := client.SaveReaction(react)

	if resp.Error != nil || resp.StatusCode < 200 || resp.StatusCode >= 300 {
		return errors.New("")
	}
	return nil
}
